import React, {Component} from 'react';
import './App.css';

import { Anchor, Nav, Button, Grommet, Header, Box, WorldMap, Main, Heading, Paragraph, Select, Layer } from 'grommet';
import { grommet } from 'grommet/themes';
import CustomEntryCard from './CustomEntryCard';

const OPTIONS = [2, 4, 6];
const OPTIONS2 = [8, 16, 20];


class App extends Component {
  state = {
    locationList: [],
    entry1: [],
    entry2: [],
    options: OPTIONS,
    options2: OPTIONS2,
    setShowResults: false,
    result: [],
    showCustomEntry: false
  }

  getResponse = async() => {
    const response = await fetch('http://localhost:5000/posts');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  }

  onCalculate = () => {
    if(this.state.entry1 && this.state.entry2){
      var temp = this.state.entry2 / this.state.entry1;
      if(temp % 1 !== 0){
        temp = Math.floor(temp);

      }      
    }
    this.setState({result: temp});
    this.setState({ showResults: true });
  }

  setShow = (x) => {
    this.setState({showCustomEntry: x})
}

  componentDidMount() {
    this.getResponse()
      .then(res => {
        const locationRes = res;
        this.setState({ locationList: locationRes });
      })
    console.log(this.state.locationList);
  }

  render() {
    const {locationList, entry1, entry2, options, options2, result, showCustomEntry, showResults} = this.state;
    return (
      <Grommet theme={grommet}>
        <div className="App">
          <Header className="App-header" pad="small" align="center">
            <Nav direction="row">
              <Anchor label="Home" href="#" />
              <Anchor label="Profile" href="#" />
            </Nav>
            <Box align="center">
              <Paragraph alignSelf="center" >Call out to API! {locationList.map((data, key) => {
                return(
                  <div key={key}>
                    {data.title}
                  </div>
                );
              })}</Paragraph>
            </Box>
          </Header>

          <Main pad="large">
            <div className="map-container" style={{height: '50%'}}>
              <Box direction="row-responsive" align="center">
                <WorldMap color="graph-1" margin="small" align="center"/>
              </Box>
            </div>
            <Box>
              <Heading>HMT</Heading>
            </Box>
            <Box direction="row">
              <div>
                <Paragraph>How many times could</Paragraph>
              </div>
              <div>
                <Select
                  options={options}
                  value={entry1}
                  size="xsmall"
                  margin="small"
                  onSearch={(searchText) => {
                    const regexp = new RegExp(searchText, 'i');
                    this.setState({ options: OPTIONS.filter(o => o.match(regexp)) });
                  }}
                  onChange={event => this.setState({
                    entry1: event.value,
                    options: OPTIONS,
                    showResults: false
                  })}
                />
              </div>
              <div>
                <Paragraph>fit into</Paragraph>
              </div>
              <div>
                <Select
                  options={locationList}
                  value={entry2}
                  size="xsmall"
                  margin="small"
                  onSearch={(searchText) => {
                    const regexp = new RegExp(searchText, 'i');
                    this.setState({ locationList: locationList.filter(o => o.match(regexp)) });
                  }}
                  onChange={ev => this.setState({
                    entry2: ev.value,
                    locationList: locationList,
                    showResults: false
                  })}
                />
              </div>

              <Paragraph>?</Paragraph>
              <Button primary margin="small" size="small" label="Calculate" onClick={this.onCalculate} />
              <Button primary margin="small" size="small" label="Custom Entry" onClick={() => this.setShow(true)} />
              {showCustomEntry ? 
                <Layer
                    onEsc={() => this.setShow(false)}
                    onClickOutside={() => this.setShow(false)}
                >
                    <CustomEntryCard> </CustomEntryCard>
                  </Layer>
                  : null
                }
            </Box>
            <Box>
              { this.state.showResults ? 
                <Heading>{entry1} could fit into {entry2} {result} times!</Heading>
              : null }
            </Box>
          </Main>
        </div>
      </Grommet>
    );
  }
}

export default App;
