import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

import { Anchor, Nav, Grommet, Header, Box, WorldMap, Main, Heading, Paragraph } from 'grommet';
import { grommet } from 'grommet/themes';


class App extends Component {
  state = {
    renderedResponse: ''
  }

  getResponse = async() => {
    const response = await fetch('/api/hello');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  }

  componentDidMount() {
    this.getResponse()
      .then(res => {
        const someData = res;
        this.setState({ renderedResponse: someData });
      })
  }

  render() {
    const {renderedResponse} = this.state;
    return (
      <Grommet theme={grommet}>
        <div className="App">
          <Header className="App-header" pad="small" align="center">
            <Nav direction="row">
              <Anchor label="Home" href="#" />
              <Anchor label="Profile" href="#" />
            </Nav>
            <div className="App">
                <h2>Call out to API!</h2>
                <p>{renderedResponse.express}</p>
              </div>
          </Header>

          <Main pad="large">
            <Box justify="center" align="center" pad="small">
              <WorldMap color="graph-1" margin="small" align="center"/>
            </Box>
            <Heading>Something</Heading>
            <Paragraph>Something about something</Paragraph>
          </Main>
        </div>
      </Grommet>
    );
  }
}

export default App;
