import React from 'react';
import ReactDOM from 'react-dom';
import L from 'leaflet';
import { MapControl, withLeaflet } from 'react-leaflet';
import { RadioButtonGroup } from 'grommet';

class RadioLocationSelect extends MapControl {  // note we're extending MapControl from react-leaflet, not Component from react
    createLeafletElement(props) {}
  componentWillMount() {
    const centerControl = L.control({position: 'bottomright'});  // see http://leafletjs.com/reference.html#control-positions for other positions
    const jsx = (
      // PUT YOUR JSX FOR THE COMPONENT HERE:
      <RadioButtonGroup
          name="doc"
          options={[1, 2]}
          value={1}
          onChange={(event) => this.props.radioLocation(event.target.value)}
      />
    );

    centerControl.onAdd = function (map) {
      let div = L.DomUtil.create('div', '');
      ReactDOM.render(jsx, div);
      return div;
    };

    this.leafletElement = centerControl;
  }
}

export default withLeaflet(RadioLocationSelect);
