import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

import { Anchor, Nav, Grommet, Header, Box, WorldMap, Main, Heading, Paragraph, Select } from 'grommet';
import { grommet } from 'grommet/themes';

const OPTIONS = ['First', 'Second', 'Third'];


class App extends Component {
  state = {
    renderedResponse: '',
    value: [], 
    options: OPTIONS 
  }

  getResponse = async() => {
    const response = await fetch('/api/hello');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  }

  componentDidMount() {
    this.getResponse()
      .then(res => {
        const someData = res;
        this.setState({ renderedResponse: someData });
      })
  }

  render() {
    const {renderedResponse, value, options} = this.state;
    return (
      <Grommet theme={grommet}>
        <div className="App">
          <Header className="App-header" pad="small" align="center">
            <Nav direction="row">
              <Anchor label="Home" href="#" />
              <Anchor label="Profile" href="#" />
            </Nav>
            <Box justify="center" align="center">
              <Paragraph alignSelf="center" >Call out to API! {renderedResponse.express}</Paragraph>
            </Box>
          </Header>

          <Main pad="large">
            <Box direction="row-responsive" justify="center" align="center" pad="small">
              <WorldMap color="graph-1" margin="small" align="center"/>
            </Box>
            <Box>
              <Heading>HMT</Heading>
            </Box>
            <Box direction="row">
              <Paragraph>How many times could</Paragraph>
              <Select
                options={options}
                value={value}
                dropHeight="small"
                onSearch={(searchText) => {
                  const regexp = new RegExp(searchText, 'i');
                  this.setState({ options: OPTIONS.filter(o => o.match(regexp)) });
                }}
                onChange={event => this.setState({
                  value: event.value,
                  options: OPTIONS,
                })}
              />
              <Paragraph>fit into</Paragraph>
            </Box>
            <Box>
              <Paragraph>This is where the results go</Paragraph>
            </Box>

          </Main>
        </div>
      </Grommet>
    );
  }
}

export default App;
