import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

import { Anchor, Nav, Button, Grommet, Header, Box, WorldMap, Main, Heading, Paragraph, Select } from 'grommet';
import { grommet } from 'grommet/themes';

const OPTIONS = [2, 4, 6];
const OPTIONS2 = [8, 16, 20];


class App extends Component {
  state = {
    renderedResponse: '',
    value: [],
    value2: [],
    options: OPTIONS,
    options2: OPTIONS2,
    setShowResults: false
  }

  getResponse = async() => {
    const response = await fetch('/api/hello');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  }

  onCalculate = () => {
    if(this.state.value && this.state.value2){
      var result = this.state.value / this.state.value2;
      this.setState({number: result});
    }
    this.setState({ showResults: true });
  }

  calculate = () => {
    if(this.state.value && this.state.value2){
      var result = this.state.value / this.state.value2;
      this.setState({number: result});
    }
  }

  componentDidMount() {
    this.getResponse()
      .then(res => {
        const someData = res;
        this.setState({ renderedResponse: someData });
      })
  }

  render() {
    const {renderedResponse, value, value2, options, options2, number, showResults} = this.state;
    return (
      <Grommet theme={grommet}>
        <div className="App">
          <Header className="App-header" pad="small" align="center">
            <Nav direction="row">
              <Anchor label="Home" href="#" />
              <Anchor label="Profile" href="#" />
            </Nav>
            <Box align="center">
              <Paragraph alignSelf="center" >Call out to API! {renderedResponse.express}</Paragraph>
            </Box>
          </Header>

          <Main pad="large">
            <div class="map-container" style={{height: '50%'}}>
              <Box direction="row-responsive" align="center">
                <WorldMap color="graph-1" margin="small" align="center"/>
              </Box>
            </div>
            <Box>
              <Heading>HMT</Heading>
            </Box>
            <Box direction="row">
              <Paragraph>How many times could</Paragraph>
              <Select
                options={options}
                value={value}
                size="xsmall"
                margin="small"
                onSearch={(searchText) => {
                  const regexp = new RegExp(searchText, 'i');
                  this.setState({ options: OPTIONS.filter(o => o.match(regexp)) });
                }}
                onChange={event => this.setState({
                  value: event.value,
                  options: OPTIONS,
                })}
              />
              <Paragraph>fit into</Paragraph>
              <Select
                options={options2}
                value2={value2}
                size="xsmall"
                margin="small"
                onSearch={(searchText) => {
                  const regexp = new RegExp(searchText, 'i');
                  this.setState({ options2: OPTIONS2.filter(o => o.match(regexp)) });
                }}
                onChange={event => this.setState({
                  value2: event.value2,
                  options2: OPTIONS2,
                })}
              />
              <Paragraph>?</Paragraph>
              <Button primary margin="small" size="small" label="Calculate" onClick={this.onCalculate} />
            </Box>
            <Box>
              { this.state.showResults ? 
                <Heading>{value} could fit into {value2} {number} times!</Heading>
              : null }
            </Box>
          </Main>
        </div>
      </Grommet>
    );
  }
}

export default App;
