import React, { Component } from 'react';
import { Layer, Card, CardBody, CardFooter, CardHeader, Button } from 'grommet';

const OPTIONS = ['First', 'Second', 'Third'];

 class CustomEntryCard extends Component {
    state = { 
        value: [], 
        options: OPTIONS,
        setShowResults: true 
    }

    setShow = (x) => {
        this.setState({setShowResults: x})
    }

    render() {
    const { options, value } = this.state;
    return (
        <Layer
        onEsc={this.setShow(false)}
        onClickOutside={this.setShow(false)}
        >
            <Card  height="small" width="large" background="light-1">
                <CardHeader pad="medium">Header</CardHeader>
                <CardBody pad="medium">Body</CardBody>
                <CardFooter pad={{horizontal: "small"}} background="light-2">   
                    <Button
                        hoverIndicator
                    />
                    <Button  hoverIndicator />
                </CardFooter>
            </Card>
        </Layer>
    );
    }
}

export default CustomEntryCard;
