import React, { Component } from 'react';
import { Card, CardBody, CardFooter, CardHeader, Button, Form, FormField, TextInput, Box, Select, Heading } from 'grommet';


class ResultsCard extends Component {
    constructor(props) {
        super(props);
        this.onCalculate = this.onCalculate.bind(this);
        this.state = {};
    }

    onCalculate = () => {
        if(this.state.entry1.area && this.state.entry2.area){
          var temp = this.state.entry2.area / this.state.entry1.area;
          if(temp % 1 !== 0){
            this.setState({decimalBlurb: true});
          }      
        }
        this.setState({ result: temp,
        locationList: this.state.locationSearchRes,
        showResults: true });
    }

    render() {
        return (
            <div>
                <Card  height="medium" align="center" width="large" background="light-1">
                    <CardHeader background="light-3" pad="medium">Custom Entry</CardHeader>
                    <CardBody pad="medium">
                    <Box>
                        { showResults ? 
                            <Heading background="neutral"> {entry1.name} could fit into {entry2.name} {Math.floor(result)} times!</Heading>
                        : null }
                        {decimalBlurb ? <Heading>({Math.round((result) * 100) /100} times to be exact.)</Heading> : null}
                    </Box>
                    </CardBody>
                    <CardFooter pad={{horizontal: "small"}} background="light-2">   
                    </CardFooter>
                </Card>

            </div>
        );
    }
}

export default ResultsCard;
