import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

import { Button, Icons, Nav, Grommet, Header } from 'grommet';
import { grommet } from 'grommet/themes';


class App extends Component {
  state = {
    renderedResponse: ''
  }

  getResponse = async() => {
    const response = await fetch('/api/hello');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  }

  componentDidMount() {
    this.getResponse()
      .then(res => {
        const someData = res;
        this.setState({ renderedResponse: someData });
      })
  }

  render() {
    const {renderedResponse} = this.state;
    return (
      <Grommet theme={grommet}>
        <div className="App">
          <Header className="App-header">
          <Button icon={<Icons.Home />} hoverIndicator />
            <div className="App">
              <h2>Call out to API!</h2>
              <p>{renderedResponse.express}</p>
            </div>
          </Header>
        </div>
      </Grommet>
    );
  }
}

export default App;
